s = input()

def check(i):
    if s[i] == "7":
        if s[i+1] == "5":
            if s[i+2] == "3":
                return 0
            else:
                return int(s[i+2]) - 3
        else:
            return int(s[i+1]) - 5
    else:
        return int(s[i]) - 7

ans = 100
for i in range(len(s) - 2):
    ans = min(ans, check(i))

print(ans)
