import sys

def solve(b):
    a = [b[0]]
    for i in range(1, len(b) - 1):
        a.append(b[i])
        if b[i] == b[i-1]:
            a.append(b[i+1])
    a.append(b[-1])
    return "".join(a)

def main():
    t = int(input())
    for i in range(t):
        b = input()
        print(solve(b))

