def team_formation(n, a):
    a.sort()
    group = 0
    for i in range(n-1):
        if a[i+1] - a[i] > 1:
            group += 1
    return group+1

q = int(input())
for i in range(q):
    n = int(input())
    a = list(map(int, input().split()))
    print(team_formation(n, a))

