import sys

input = sys.stdin.readline

def main():
    q = int(input())
    for _ in range(q):
        n = int(input())
        a = list(map(int, input().split()))
        a.sort()
        if n == 1:
            print(1)
        else:
            i, j = 0, 1
            ans = 1
            while j < n:
                if a[j] - a[i] > 1:
                    i = j
                    ans += 1
                j += 1
            print(ans)

if __name__ == "__main__":
    main()
