import sys

def is_good(a):
    if len(a) % 6 != 0:
        return False
    if len(a) == 0:
        return True
    if len(a) == 6:
        if a == [4, 8, 15, 16, 23, 42]:
            return True
        else:
            return False
    for i in range(len(a) - 6):
        if a[i] == 4 and a[i + 1] == 8 and a[i + 2] == 15 and a[i + 3] == 16 and a[i + 4] == 23 and a[i + 5] == 42:
            return is_good(a[:i] + a[i + 6:])
    return False

def solve(a):
    if is_good(a):
        return 0
    for i in range(len(a)):
        if is_good(a[:i] + a[i + 1:]):
            return 1 + solve(a[:i] + a[i + 1:])

