def is_prime(n):
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True


def factorize(n):
    factors = []
    for i in range(2, int(n ** 0.5) + 1):
        while n % i == 0:
            factors.append(i)
            n //= i
    if n > 1:
        factors.append(n)
    return factors


def solve(n):
    if n == 2:
        return "NO"
    if n == 3:
        return "YES\n2 1 3"
    if is_prime(n):
        return "NO"
    factors = factorize(n)
    if len(factors) == 1:
        return "NO"
    if len(factors) == 2:
        return "YES\n{} {} {}".format(factors[0], factors[0], factors[1])
    if len(factors) == 3:
        return "YES\n{} {} {}".format(factors[0], factors[1], factors[2])


def main():
    t = int(input())
    for i in range(t):
        n = int(input())
        print(solve(n))


if __name__ == "__main__":
    main()

