def isPrime(n):
    if n == 2 or n == 3:
        return True
    elif n < 2 or n%2 == 0:
        return False
    elif n < 9:
        return True
    elif n%3 == 0:
        return False
    else:
        r = int(n**0.5)
        f = 5
        while f <= r:
            if n%f == 0:
                return False
            if n%(f+2) == 0:
                return False
            f +=6
        return True

def isSquare(n):
    if n == 1:
        return True
    elif n < 0:
        return False
    else:
        return int(n**0.5)**2 == n

def find_factors(n):
    factors = []
    # if n is prime, then it is the only factor
    if isPrime(n):
        factors.append(n)
        return factors
    # if n is a square, then its square root is a factor
    if isSquare(n):
        factors.append(int(n**0.5))
        return factors
    # if n has no prime factors, then it is a product of two primes
    if len(prime_factors(n)) == 0:
        for i in range(2, n):
            if isPrime(i) and n%i == 0:
                factors.append(i)
                factors.append(int(n/i))
                return factors
    # if n has prime factors, then it is a product of a prime and a square
    else:
        factors.append(prime_factors(n)[0])
        factors.append(int(n/prime_factors(n)[0]))
        return factors

def prime_factors(n):
    factors = []
    if isPrime(n):
        factors.append(n)
        return factors
    else:
        for i in range(2, n):
            if isPrime(i) and n%i == 0:
                factors.append(i)
    return factors

def find_three_factors(n):
    factors = find_factors(n)
    if len(factors) == 2:
        if isSquare(factors[1]):
            return [factors[0], int(factors[1]**0.5), int(factors[1]**0.5)]
        else:
            return []
    return factors

