def solve(s):
    n = len(s)
    cnt = [0] * (n + 1)
    for i in range(n):
        cnt[i + 1] = cnt[i] + (1 if s[i] == 'a' else -1)
    a, b = 0, 0
    for i in range(1, n + 1, 2):
        if cnt[i] < 0:
            a += 1
        if cnt[i] > 0:
            b += 1
    return min(a, b)

