def is_round(n):
    n = str(n)
    if len(n) == 1:
        return True
    for i in range(1, len(n)):
        if n[i] != '0':
            return False
    return True


def round_numbers(n):
    if is_round(n):
        return [n]
    else:
        for i in range(1, len(str(n))):
            if is_round(int(str(n)[:i])):
                return [int(str(n)[:i])] + round_numbers(int(str(n)[i:]))


def main():
    t = int(input())
    for i in range(t):
        n = int(input())
        print(len(round_numbers(n)))
        print(*round_numbers(n))


if __name__ == "__main__":
    main()

