def solve(a, b):
    if a == b:
        return 0

    c = [0] * 26
    for i in range(n):
        c[ord(a[i]) - ord('a')] += 1
        c[ord(b[i]) - ord('a')] -= 1
    if sum(c) != 0:
        return -1

    count = 0
    for i in range(n):
        if a[i] != b[i]:
            break
        count += 1
    for i in range(n - 1, -1, -1):
        if a[i] != b[i]:
            break
        count += 1
    return n - count

print(solve(a, b))

