def solve(a,b):
    assert(len(a) == len(b))
    n = len(a)
    ans = 0
    for i in range(n//2):
        if a[i] != b[i]:
            ans += 1
        if a[n-1-i] != b[n-1-i]:
            ans += 1
    if n%2 == 1 and a[n//2] != b[n//2]:
        ans += 1
    return ans

