def min_product(a, b, x, y, n):
    if a == x and b == y:
        return a * b
    elif a == x:
        return min(a * b, a * (b - min(b - y, n)))
    elif b == y:
        return min(a * b, (a - min(a - x, n)) * b)
    else:
        return min(a * b, a * (b - min(b - y, n)), (a - min(a - x, n)) * b, (a - min(a - x, n)) * (b - min(b - y, n)))

