class Solution:
    def largestSumAfterKNegations(self, A: List[int], K: int) -> int:
        A.sort()
        i = 0
        while i < len(A) and K > 0:
            if A[i] < 0:
                A[i] = -A[i]
                K -= 1
            else:
                break
            i += 1

        return sum(A) - (K%2)*min(A)*2

class Solution:
    def largestSumAfterKNegations(self, A: List[int], K: int) -> int:
        if not A:
            return 0
        A.sort()
        i = 0
        while i < len(A) and i < K and A[i] < 0:
            A[i] = -A[i]
            i += 1
        if i == len(A):
            return sum(A) - 2 * min(A)
        if i == K:
            return sum(A)
        if (K - i) % 2 == 1:
            if A[i] < A[i-1]:
                A[i] = -A[i]
            else:
                A[i-1] = -A[i-1]
        return sum(A)

class Solution:
    def largestSumAfterKNegations(self, A: List[int], K: int) -> int:
        A.sort()
        for i in range(len(A)):
            if A[i] < 0 and K > 0:
                A[i] = -A[i]
                K -= 1
        if K > 0:
            if K % 2 == 1:
                A.sort()
                A[0] = -A[0]
        return sum(A)

class Solution:
    def largestSumAfterKNegations(self, A: List[int], K: int) -> int:
        # 先把负数变成正数，然后排序，每次取最小的数加上1，把最小数变成0，每次加1就是将最小的数变成最大的数，直到K为0
        A = [i if i>0 else -i for i in A]
        A.sort()
        for i in range(len(A)):
            if K == 0:
                return sum(A)
            else:
                A[i] += 1
                K -= 1
        return sum(A)

class Solution(object):
    def largestSumAfterKNegations(self, A, K):
        """
        :type A: List[int]
        :type K: int
        :rtype: int
        """
        A.sort()
        for i in range(K):
            if A[i] < 0:
                A[i] = -A[i]
            else:
                if i != 0 and A[i] > A[i-1]:
                    A[i-1] = -A[i-1]
                else:
                    A[i] = -A[i]
        return sum(A)

class Solution(object):
    def largestSumAfterKNegations(self, A, K):
        """
        :type A: List[int]
