from typing import List


class Solution:
    def canThreePartsEqualSum(self, A: List[int]) -> bool:
        total, part = sum(A), 0
        if total % 3 != 0:
            return False

        for i, a in enumerate(A):
            part += a
            if part == total // 3:
                part = 0
                if i == len(A) - 2:
                    return True
                continue

        return False


