def collect_candies(N, A):
    dp = [[0 for i in range(N + 1)] for j in range(2)]
    for i in range(1, 2):
        for j in range(1, N + 1):
            dp[i][j] = max(dp[i - 1][j], dp[i][j - 1]) + A[i - 1][j - 1]
    return dp[1][N]


