import sys

def main():
    n = int(input())
    x = [int(i) for i in input().split()]
    v = [int(i) for i in input().split()]

    # calculate distance of each point
    dist = [0] * n
    for i in range(n):
        for j in range(n):
            if i == j:
                continue
            dist[i] += abs(x[i] - x[j])

    # calculate the sum of distances
    total_dist = 0
    for i in range(n):
        for j in range(n):
            if i == j:
                continue
            if v[i] > v[j]:
                total_dist += dist[i]
            else:
                total_dist += dist[j]

    print(total_dist)

if __name__ == "__main__":
    main()

def distance(x1, v1, x2, v2):
    if v1 == v2:
        return 0
    else:
        if (x2-x1)%(v1-v2) == 0:
            return (x2-x1)/(v1-v2)
        else:
            return -1

def distance_sum(x, v):
    s = 0
    for i in range(len(x)):
        for j in range(i+1, len(x)):
            d = distance(x[i], v[i], x[j], v[j])
            if d >= 0:
                s += d
    return s

