N = int(input())
A = list(map(int, input().split()))

for i in range(N):
    if i == 0:
        cost = A[i+1] - A[i] + A[-1] - A[i+1]
    elif i == N - 1:
        cost = A[i] - A[0] + A[i-1] - A[0]
    else:
        cost = abs(A[i-1] - A[i+1]) + abs(A[-1] - A[0])
    print(cost)

N = int(input())
A = [int(i) for i in input().split()]

for i in range(N):
    if i == 0:
        print(sum(A[i+1:]) + abs(A[i+1]))
    elif i == N-1:
        print(sum(A[:i]) + abs(A[i-1]))
    else:
        print(sum(A[:i]) + abs(A[i-1] - A[i+1]) + sum(A[i+1:]))

def sightseeing(N, A):
    res = []
    for i in range(N):
        res.append(sum(map(abs, A[:i] + A[i+1:])) + abs(A[i]))
    return res

def main():
    N = int(input())
    A = list(map(int, input().split()))
    print(*sightseeing(N, A))

if __name__ == '__main__':
    main()

