import math

def is_square(n):
    return math.sqrt(n).is_integer()

def main():
    a, b = map(int, input().split())
    ab = int(str(a) + str(b))
    if is_square(ab):
        print("Yes")
    else:
        print("No")

if __name__ == '__main__':
    main()

