import sys

def main():
    N, M, X = [int(x) for x in sys.stdin.readline().split()]
    A = [int(x) for x in sys.stdin.readline().split()]

    # cost to reach 0 from X
    cost_to_0 = 0
    for i in range(X-1, -1, -1):
        if i in A:
            cost_to_0 += 1
    # cost to reach N from X
    cost_to_N = 0
    for i in range(X+1, N+1):
        if i in A:
            cost_to_N += 1
    # print the smaller cost
    print min(cost_to_0, cost_to_N)

if __name__ == "__main__":
    main()

