import sys

def main():
    N = int(sys.stdin.readline())
    X = list(map(int, sys.stdin.readline().split()))
    X.sort()

    for i in range(0, N):
        if i == 0:
            print(X[N//2])
        elif i == N//2:
            print(X[N//2-1])
        else:
            print(X[N//2])

if __name__ == '__main__':
    main()

