import sys

def get_median(l):
    if len(l) % 2 == 0:
        return l[int(len(l)/2)-1]
    else:
        return l[int(len(l)/2)]

def main():
    N = int(input())
    X = list(map(int, input().split()))
    X.sort()
    for i in range(N):
        if i % 2 == 0:
            print(get_median(X[i//2:N-(i//2)]))
        else:
            print(get_median(X[i//2+1:N-(i//2)]))

if __name__ == '__main__':
    main()
