import heapq

N, M, K = map(int, input().split())
A = list(map(int, input().split()))
B = list(map(int, input().split()))

heapq.heapify(A)
heapq.heapify(B)

ans = 0
while A and B and K > 0:
    a = heapq.heappop(A)
    b = heapq.heappop(B)
    if a < b:
        if a <= K:
            ans += 1
            K -= a
        else:
            break
    else:
        if b <= K:
            ans += 1
            K -= b
        else:
            break

while A and K > 0:
    a = heapq.heappop(A)
    if a <= K:
        ans += 1
        K -= a
    else:
        break

while B and K > 0:
    b = heapq.heappop(B)
    if b <= K:
        ans += 1
        K -= b
    else:
        break

print(ans)

n, m, k = map(int, input().split())
a = list(map(int, input().split()))
b = list(map(int, input().split()))

if sum(a) <= k:
    print(n)
    exit()
if sum(b) <= k:
    print(m)
    exit()

sa = 0
ea = 0
sb = 0
eb = 0

count = 0

while sa < n and sb < m:
    if a[sa] < b[sb]:
        if sa == ea:
            ea += 1
            sa += 1
        else:
            if a[sa] + a[ea] <= k:
                count += ea - sa + 1
                ea += 1
                sa += 1
            else:
                break
    else:
        if sb == eb:
            eb += 1
            sb += 1
        else:
            if b[sb] + b[eb] <= k:
                count += eb - sb + 1
                eb += 1
                sb += 1
            else:
                break

if sa == n:
    while sb < m:
        if sb == eb:
            eb += 1
            sb += 1
        else:
            if b[sb] + b[eb] <= k:
                count += eb - sb + 1
                eb += 1
                sb += 1
            else:
                break
else:
    while sa < n:
        if sa == ea:
            ea += 1
            sa += 1
        else:
            if a[sa] + a[ea] <= k:
                count += ea - sa + 1
                ea += 1
                sa += 1
            else:
                break

print(count)
