import sys

def main():
    a, b, c, x, y = map(int, sys.stdin.readline().split())
    cost = 0
    if a < c and b < c:
        cost = a * x + b * y
    else:
        if x >= y:
            cost += b * 2 * y
            x -= y
            if a < c:
                cost += a * x
            else:
                cost += c * x
        else:
            cost += a * 2 * x
            y -= x
            if b < c:
                cost += b * y
            else:
                cost += c * y
    print(cost)

if __name__ == '__main__':
    main()

