from collections import deque

def find_max_len(s):
  start_idx = deque()
  end_idx = deque()
  for i, c in enumerate(s):
    if c == 'A':
      start_idx.append(i)
    if c == 'Z':
      end_idx.append(i)
  return end_idx[0] - start_idx[-1] + 1

def main():
  s = input()
  print(find_max_len(s))

if __name__ == '__main__':
  main()

from sys import stdin
from collections import deque

def main():
    s = stdin.readline().rstrip()
    s_len = len(s)
    ans = 0
    start = deque()
    end = deque()

    for i in range(s_len):
        if s[i] == "A":
            start.append(i)
        if s[i] == "Z":
            end.append(i)

    while start:
        if start[0] < end[0]:
            ans = max(ans, end[0] - start[0] + 1)
            start.popleft()
        else:
            end.popleft()

    print(ans)

if __name__ == '__main__':
    main()

