def solve(n, a_list):
    max_count = 0
    for a in a_list:
        count = 0
        while a % 2 == 0:
            count += 1
            a /= 2
        max_count = max(max_count, count)
    return max_count

def main():
    n = int(input())
    a_list = list(map(int, input().split()))
    print(solve(n, a_list))

if __name__ == '__main__':
    main()

