import sys

def main():
    n, k = map(int, sys.stdin.readline().split())
    h = list(map(int, sys.stdin.readline().split()))
    h.sort()

    cnt = 0
    s = k
    for i in range(n):
        if h[i] > s:
            cnt += h[i] - s
            s = 0
        else:
            s -= h[i]
    print(cnt)

if __name__ == "__main__":
    main()

N, K = map(int, input().split())
H = list(map(int, input().split()))

H.sort()
ans = 0
for i in range(N):
    if K > 0:
        if H[i] - K <= 0:
            ans += H[i]
            K -= H[i]
        else:
            ans += K
            K = 0
    else:
        ans += H[i]

print(ans)

n,k = map(int, input().split())
h = list(map(int, input().split()))

h.sort()

ans = 0
for i in range(n):
    h[i] -= ans
    if h[i] > 0:
        if k > 0:
            k -= 1
            h[i] = 0
        else:
            ans += h[i]

print(ans)
