def get_digits(num):
    return [int(i) for i in str(num)]

def digit_sum(num):
    return sum(get_digits(num))

def is_within_range(num, a, b):
    return a <= digit_sum(num) <= b

def solve(n, a, b):
    return sum([i for i in range(1, n+1) if is_within_range(i, a, b)])

print(solve(n, a, b))

