def main():
    n = int(input())
    x = 0
    y = 0
    for _ in range(n):
        t, x, y = map(int, input().split())
        if t < abs(x) + abs(y) or (t - abs(x) - abs(y)) % 2 != 0:
            print('No')
            return
    print('Yes')


if __name__ == '__main__':
    main()

import sys

def main():
    n = int(sys.stdin.readline())
    xy = []
    for i in range(n):
        xy.append(list(map(int, sys.stdin.readline().split())))
    for i in range(n-1):
        if xy[i][1] == xy[i+1][1] and xy[i][2] == xy[i+1][2]:
            print("No")
            return
    print("Yes")

if __name__ == '__main__':
    main()
