import sys

def findFloor(n, x):
    floor = 1
    if n == 1 or n == 2:
        return floor
    else:
        n -= 2
        while n > 0:
            floor += 1
            n -= x
    return floor

if __name__ == "__main__":
    t = int(input())
    for i in range(t):
        n, x = map(int, input().split())
        print(findFloor(n, x))

