def main():

    num = int(input())
    for i in range(num):
        n, s = map(int, input().split())
        if n == 1:
            print(0)
        else:
            num_of_moves = 0
            while get_sum_of_digits(n) > s:
                num_of_moves += 1
                n += 1
            print(num_of_moves)

def get_sum_of_digits(n):
    sum_of_digits = 0
    while n != 0:
        sum_of_digits += n % 10
        n //= 10
    return sum_of_digits

if __name__ == '__main__':
    main()

def is_possible(n, s):
    if n == 0:
        return True
    elif s <= 0:
        return False
    else:
        return is_possible(n // 10, s - n % 10)

def solve(n, s):
    moves = 0
    while not is_possible(n, s):
        n += 1
        moves += 1
    return moves

if __name__ == '__main__':
    t = int(input())
    for _ in range(t):
        n, s = map(int, input().split())
        print(solve(n, s))

