def calc(n,s):
    if n <= s:
        return 0
    else:
        n_sum = sum_of_digits(n)
        if n_sum <= s:
            return 0
        else:
            return 1 + calc(n+1,s)

def sum_of_digits(n):
    s = 0
    while n > 0:
        s += n % 10
        n = n // 10
    return s

