def main():
    t = int(input())
    for i in range(t):
        n = int(input())
        a = list(map(int, input().split()))
        moves = 0
        a_sum = 0
        b_sum = 0
        a_sum_last = 0
        b_sum_last = 0
        while a != []:
            if moves % 2 == 0:
                for i in range(len(a)):
                    a_sum += a[i]
                    if a_sum > b_sum_last:
                        moves += 1
                        a = a[i+1:]
                        break
            else:
                for i in range(len(a)-1, -1, -1):
                    b_sum += a[i]
                    if b_sum > a_sum_last:
                        moves += 1
                        a = a[:i]
                        break
            a_sum_last = a_sum
            b_sum_last = b_sum
            a_sum = 0
            b_sum = 0
        print(moves, a_sum_last, b_sum_last)

