def secret_array(n, x, y):
    if n == 2:
        return str(x) + ' ' + str(y)
    else:
        a = [x, y]
        diff = (y - x) / (n - 1)
        for i in range(2, n):
            a.append(int(a[i - 1] + diff))
        return ' '.join(map(str, a))

