import sys

def solve():
    n, x, y = [int(x) for x in sys.stdin.readline().split()]
    if n == 2:
        return [x, y]

    if y - x == n - 1:
        return [i for i in range(y, y - n, -1)]

    d = (y - x) // (n - 1)
    if y - x % (n - 1) == 0:
        return [x + i * d for i in range(n)]

    return [x + i * d + 1 for i in range(n)]

t = int(sys.stdin.readline())
for i in range(t):
    print(' '.join([str(x) for x in solve()]))
