def count_elements(arr, divisor):
    count = 0
    for i in arr:
        if i % divisor == 0:
            count += 1
    return count

def main():
    t = int(input())
    for _ in range(t):
        n = int(input())
        arr = list(map(int, input().split()))
        if n == 1:
            print(1 if arr[0] % 3 == 0 else 0)
        else:
            if sum(arr) % 3 == 0:
                print(n)
            else:
                print(max(count_elements(arr, 3),
                           count_elements(arr, 2)))

