import sys

class TestCase:
    def __init__(self, n, k):
        self.n = n
        self.k = k

class TestSet:
    def __init__(self, test_cases):
        self.test_cases = test_cases

def read_test_set(lines):
    test_cases = []
    for i in range(int(lines[0])):
        test_case = TestCase(*[int(n) for n in lines[i+1].split(" ")])
        test_cases.append(test_case)
    return TestSet(test_cases)

def solve_test_case(test_case):
    n = test_case.n
    k = test_case.k
    if n % 2 == 0:
        if k % 2 == 0:
            return "YES\n" + " ".join([str(n//k)] * k)
        else:
            return "NO"
    else:
        if k % 2 == 1:
            return "YES\n" + " ".join([str(n//k)] * k)
        else:
            return "NO"

def solve_test_set(test_set):
    return "\n".join([solve_test_case(test_case) for test_case in test_set.test_cases])

def main():
    lines = sys.stdin.readlines()
    test_set = read_test_set(lines)
    solution = solve_test_set(test_set)
    print(solution)

if __name__ == "__main__":
    main()

