def main():
    n = int(input())
    a = [int(i) for i in input().split()]
    stools = [0] * n
    for i in range(n-1):
        if a[i] <= a[i+1]:
            continue
        else:
            stools[i+1] = a[i] - a[i+1] + 1
            a[i+1] = a[i] + 1
    print(sum(stools))

if __name__ == '__main__':
    main()

def main():
    n = int(input())
    h = [int(i) for i in input().split()]
    dp = [0] * n
    dp[0] = h[0]
    for i in range(1, n):
        if dp[i - 1] < h[i]:
            dp[i] = h[i]
        else:
            dp[i] = dp[i - 1]
    print(sum(dp))

if __name__ == '__main__':
    main()
