import sys

def main():
    lines = sys.stdin.readlines()
    for i, line in enumerate(lines):
        n, shuffle = line.split()
        n = int(n)
        print("{} {}".format(i+1, perfect_shuffle(n, shuffle)))

def perfect_shuffle(n, shuffle):
    if shuffle == "out":
        if n % 2 == 0:
            return n // 2
        else:
            return n // 2 + 1
    else:
        if n % 2 == 0:
            return n - 1
        else:
            return n

