def main():
    n = int(input())
    grid = []
    for _ in range(n):
        grid.append(input())

    # Check rows
    for row in grid:
        if not check_row(row):
            print(0)
            return

    # Check columns
    for i in range(n):
        column = ""
        for j in range(n):
            column += grid[j][i]
        if not check_row(column):
            print(0)
            return

    # If all checks pass
    print(1)

def check_row(row):
    # Check row has equal number of black and white squares
    if row.count('B') != row.count('W'):
        return False

    # Check no row has 3 or more consecutive squares of the same color
    for i in range(len(row) - 2):
        if row[i] == row[i+1] and row[i] == row[i+2]:
            return False

    return True

