def is_multigram(word):
    if len(word) == 1:
        return False
    if len(word) == 2:
        return False
    if len(word) == 3:
        return False
    elif len(word) == 4:
        return word[0] == word[1] and word[2] == word[3]
    else:
        return is_multigram(word[0:len(word)//2]) and is_multigram(word[len(word)//2:])

def root_of_multigram(word):
    if is_multigram(word):
        if len(word) == 4:
            return word[0]
        else:
            return root_of_multigram(word[0:len(word)//2])
    else:
        return -1

