import sys
from collections import Counter

word = sys.stdin.readline().strip()

if len(word) % 2 == 1:
    print("-1")
else:
    word_len = len(word)
    half_word_len = word_len // 2
    half_word = word[:half_word_len]
    half_word_count = Counter(half_word)
    for i in range(half_word_len, word_len):
        half_word_count[word[i]] -= 1
        if half_word_count[word[i]] == 0:
            del half_word_count[word[i]]
    if len(half_word_count) == 0:
        print(half_word)
    else:
        print("-1")

