            if grid[i][j] == '#':
                continue
            if i > 0 and grid[i-1][j] != '#':
                grid[i][j] = grid[i-1][j]
            if j > 0 and grid[i][j-1] != '#':
                grid[i][j] = grid[i][j-1]
    words = []
    for i in range(R):
        for j in range(C):
            if grid[i][j] == '#':
                continue
            cur = grid[i][j]
