def is_valid(i, j, r, c):
    if i < 0 or j < 0 or i >= r or j >= c:
        return False
    return True

def is_letter(grid, i, j, r, c):
    if is_valid(i, j, r, c) and grid[i][j] != '#':
        return True
    return False

def find_word(grid, r, c):
    min_word = 'z'
    for i in range(r):
        for j in range(c):
            if is_letter(grid, i, j, r, c):
                # print(i,j)
                if is_letter(grid, i+1, j, r, c):
                    word = grid[i][j] + grid[i+1][j]
                    if word < min_word:
                        min_word = word
                if is_letter(grid, i, j+1, r, c):
                    word = grid[i][j] + grid[i][j+1]
                    if word < min_word:
                        min_word = word
    return min_word

