import sys

def sub_45(h, m):
    if m < 45:
        return (h - 1) % 24, m + 15
    else:
        return h, m - 45

def main():
    h, m = map(int, sys.stdin.readline().strip().split())
    h, m = sub_45(h, m)
    print("{} {}".format(h, m))

if __name__ == '__main__':
    main()

