            subtract_time(line)

def add_time(line):
    time = int(line[3]) + int(line[2])*60 + int(line[1])
    if time > 1439:
        time = time - 1440
    hour = time // 60
    minute = time % 60
    print(hour, minute)

def subtract_time(line):
    time = int(line[3]) + int(line[2])*60 - int(line[1])
    if time < 0:
        time = time + 1440
    hour = time // 60
    minute = time % 60
    print(hour, minute)

