def change_time(direction, minutes, hours, minutes_past):
    if direction == 'F':
        if minutes_past + minutes >= 60:
            hours += 1
            minutes_past = (minutes_past + minutes) % 60
        else:
            minutes_past += minutes
    else:
        if minutes_past - minutes < 0:
            hours -= 1
            minutes_past = 60 - (minutes - minutes_past) % 60
        else:
            minutes_past -= minutes
    return hours, minutes_past

