import sys

def min_moves(a, b, c):
    if b % a == 0 and c % b == 0:
        return 0
    else:
        if b % a != 0:
            if b % a > a // 2:
                return 1 + min_moves(a, b + 1, c)
            else:
                return 1 + min_moves(a, b - 1, c)
        else:
            if c % b > b // 2:
                return 1 + min_moves(a, b, c + 1)
            else:
                return 1 + min_moves(a, b, c - 1)

