import sys

def min_div(a, b, c):
    if b % a == 0 and c % b == 0:
        return 0
    if a == b and b == c:
        return 0
    if a == b:
        return min(b - a + min_div(b, b + 1, c), a - b + min_div(b - 1, b, c))
    if b == c:
        return min(b - a + min_div(b, b + 1, b + 1), a - b + min_div(b - 1, b, b))
    if a < b < c:
        return min(b - a + min_div(b, b + 1, c), a - b + min_div(b - 1, b, c))
    if b < a < c:
        return min(b - a + min_div(b, b + 1, c), a - b + min_div(b - 1, b, c))
    if a < c < b:
        return min(b - a + min_div(b, b + 1, b + 1), a - b + min_div(b - 1, b, c))

def main():
    t = int(sys.stdin.readline().strip())
    for i in range(t):
        a, b, c = [int(x) for x in sys.stdin.readline().strip().split()]
        print(min_div(a, b, c))
        print(a, b, c)

