n = int(input())
a = list(map(int, input().split()))


def is_good(arr):
    if len(arr) == 1:
        return False
    return sum(arr) - arr[0] == arr[0]


def find_good_indices(arr):
    good_indices = []
    for i in range(len(arr)):
        new_arr = arr.copy()
        new_arr.pop(i)
        if is_good(new_arr):
            good_indices.append(i+1)
    return good_indices


