def main():
    n = int(input())
    sum_of_first_half = (n // 2) * (n // 2 + 1)
    if n % 2 == 0:
        sum_of_first_half -= n // 2
    sum_of_second_half = n * (n + 1) // 2 - sum_of_first_half
    print(abs(sum_of_first_half - sum_of_second_half))


if __name__ == "__main__":
    main()

